/* Contract for ensuring that a user has enough of a balance to cover an action.
 *
 * Fractional numbers are handled at a fixed precision determined by the
 * precision parameter.
 * 
 */

pragma solidity ^0.4.17;

import './interfaces/IActivityLimiter.sol';
import './interfaces/IBalanceManager.sol';
import './interfaces/IContractProvider.sol';
import './interfaces/IEntityRegistry.sol';

import './ContractProviderEnabled.sol';
import './MultipleOwners.sol';
import './WithActions.sol';
import './WithTokenBalance.sol';

import 'zeppelin-solidity/contracts/math/SafeMath.sol';
import 'zeppelin-solidity/contracts/math/Math.sol';


contract ActivityLimiter is IActivityLimiter, MultipleOwners, ContractProviderEnabled, WithTokenBalance {
    using SafeMath for uint256;

    // Configuration
    uint256 public intervalLength = 1 weeks;
    uint256 public capacity = 10 ** 6; // Set this to the total token supply
    uint256 public precision = 10 ** 6;  // Precision for fractions
    uint256 public beta = 1111111; // [ (10 / 9) * precision ]
    mapping(uint16 => uint256) public weightTable;

    // The multiplier applied to token balances 
    uint256 public tokenMultiplier = precision; // Start with multiplier = 1

    // Keep track of aggregate token consumption
    uint256 public intervalStart = 0;
    uint256 public sumConsumptionThisInterval = 0;

    // Keep track of individual token consumption
    mapping(address => TokenBalance) public consumption;

    // Interval rolls over
    event LogNewInterval(uint256 prevTokenConsumption, uint256 indexed startTime);

    // Update to parameters
    event LogParameterUpdate(uint256 indexed timestamp, uint256 _intervaLength, uint256 _capacity, uint256 _precision, uint256 _beta);

    // Adjust the length of the ofn "day" and other system parameters
    function setParameters(uint256 _intervalLength, uint256 _capacity, uint256 _precision, uint256 _beta) 
        public
        onlyOwner
        returns (bool)
    {
        require(_intervalLength > 0);
        require(_capacity > 0);
        require(_precision > 0);
        require(_beta > 0);

        intervalLength = _intervalLength;
        capacity = _capacity;
        precision = _precision;
        beta = _beta;

        LogParameterUpdate(now, _intervalLength, _capacity, _precision, _beta);

        return true;
    }

    // Construct the contract, setting the default weights
    function ActivityLimiter()
        public
    {
        weightTable[ uint16(NetworkActivity.AddAsset) ]                        = 1000;
        weightTable[ uint16(NetworkActivity.UpdateAsset) ]                     = 750;
        weightTable[ uint16(NetworkActivity.ManageShareholder) ]               = 1000;
        weightTable[ uint16(NetworkActivity.RegisterEntity) ]                  = 100;
        weightTable[ uint16(NetworkActivity.ModifyEntity) ]                    = 100;
        weightTable[ uint16(NetworkActivity.InitiateTransaction) ]             = 10;
        weightTable[ uint16(NetworkActivity.ReverseTransaction) ]              = 500;
        weightTable[ uint16(NetworkActivity.ApproveTransaction) ]              = 50;
        weightTable[ uint16(NetworkActivity.ApproveTransactionAsParticipant) ] = 1;
    }


    // Update a weight
    function setWeight(uint16 activity, uint256 weight) 
        public
        onlyOwner
        returns (bool)
    {
        weightTable[activity] = weight;
        return true;
    }


    // Multiply x by the current tokenMultiplier, updating the token multiplier
    // in the process.
    //
    // Unlucky users who are active around the interval turnover time incur
    // greater gas costs.
    function getMultiple(uint256 x)
        public
        returns (uint256)
    {

        if (intervalStart + intervalLength < now) {
            // Recompute the capacity and multiplier

            // In natural units:
            // impliedCapacity = max { capacity, sumConsumptionThisInterval * beta * tokenMultiplier } 
            uint256 impliedCapacity = 
                Math.max256(capacity,
                    sumConsumptionThisInterval
                    .mul(beta)
                    .mul(tokenMultiplier)
                    .div(precision.mul(precision))
                    ); 

            // altAlpha = impliedCapacity / capacity
            uint256 altAlpha = 
                impliedCapacity
                .mul(precision)
                .div(capacity);

            // tokenMultiplier = max { tokenMultiplier, altAlpha }
            tokenMultiplier = Math.max256(tokenMultiplier, altAlpha);
            capacity = impliedCapacity;

            // Update time-tracking parameters 
            intervalStart = now;
            LogNewInterval(sumConsumptionThisInterval, intervalStart);
            sumConsumptionThisInterval = 0;
        } 

        return x.mul(tokenMultiplier).div(precision);
    }

    // Attempt to perform an action, throwing a reverting exception if it is not possible
    function performAction(NetworkActivity activity)
        public
    {

        // Fetch user's balance and the weight of the activity that they are trying to perform
        address balanceManagerAddr = IContractProvider(cp).contracts("balance_manager");
        var (balance, queryTime) = IBalanceManager(balanceManagerAddr).getDistribution(tx.origin, 'OFN');

        // The user must have a fresh token balance
        require(queryTime + intervalLength >= now);

        // Fetch user's token consumption, zeroing if it is not fresh enough
        TokenBalance storage userConsumption = consumption[tx.origin];
        if (userConsumption.queryTime < intervalStart) {
            userConsumption.balance = 0;
            userConsumption.queryTime = intervalStart;
        }

        uint256 weight = weightTable[ uint16(activity) ];

        // Make sure the token holdings cover the activity
        require(balance.mul(tokenMultiplier).div(precision).sub(userConsumption.balance) > weight);

        // Add weight to the consumption
        userConsumption.balance += weight; 

        // Update total consumption
        sumConsumptionThisInterval += weight;

    }

}
