/* Source for main-net token balances 
 * 
 * Version 1.0: The OFN oracle operated by CFX sets the token balances
 * 
 * Version N: This contract is replaced via the contract provider by a contract
 * which determines main-chain token balances by some distributed mechanism,
 * possibly an extension to the network consensus rules allowing main-chain
 * state to be represented on the OFN chain.
 *
 */

pragma solidity ^0.4.17;

import './MultipleOwners.sol';
import './WithTokenBalance.sol';
import './ContractProviderEnabled.sol';

import './interfaces/IBalanceManager.sol';

contract BalanceManager is IBalanceManager, ContractProviderEnabled, MultipleOwners, WithTokenBalance {

  // Record of the update
  event LogBalanceUpdate(address indexed agent, address indexed tokenAddr, uint256 quantity);

  // Map an address and a token-id to a token balance 
  mapping(address => mapping(address => TokenBalance)) public distribution;

  // Update the token balance
  function updateTokenBalance(address agent, address tokenAddr, uint256 quantity)
      public 
      onlyOwner
      returns (bool)
  {
      require(agent != address(0));
      require(tokenAddr != address(0));

      distribution[agent][tokenAddr] = TokenBalance(quantity, now);
      LogBalanceUpdate(agent, tokenAddr, quantity);

      return true;
  }

  // Retrieve the token balance
  function getDistribution(address agent, address tokenAddr)
      public
      view
      returns (uint256 balance, uint256 time)
  {
      return (distribution[agent][tokenAddr].balance, distribution[agent][tokenAddr].queryTime);
  } 

}
