pragma solidity ^0.4.17;


import './interfaces/IContractProviderEnabled.sol';
import './MultipleOwners.sol';


contract ContractProvider is MultipleOwners {
    mapping(bytes32 => address) public contracts;
    mapping(address => bytes32) public contractsByAddr;

    function addContract(bytes32 name, address addr) public onlyOwner returns(bool) {
        require(addr != 0x0);
        require(name.length > 0);

        if (IContractProviderEnabled(addr).setContractProvider(this)) {
            contracts[name] = addr;
            contractsByAddr[addr] = name;
            return true;
        } else {
            return false;
        }
    }

    function updateContract(bytes32 name, address addr) public onlyOwner returns(bool) {
        require(contracts[name] != address(0));
        require(contractsByAddr[addr] != '');

        contracts[name] = addr;
        contractsByAddr[addr] = name;
        return true;
    }

    function updateContractProvider(bytes32 name, address newCP) public onlyOwner returns(bool) {
        require(name != '');
        require(newCP != address(this) && newCP != address(0));

        IContractProviderEnabled(contracts[name]).setContractProvider(newCP);
        return true;
    }

    function removeContract(bytes32 name) public onlyOwner returns(bool) {
        require(name.length > 0);

        if(contracts[name] == 0x0) {
            return false;
        } else {
            delete contractsByAddr[contracts[name]];
            delete contracts[name];
            return true;
        }
    }
}
