pragma solidity ^0.4.17;


import './ContractProviderEnabled.sol';
import './Limited.sol';
import './WithActions.sol';

import './interfaces/IContractProvider.sol';
import './interfaces/IActivityLimiter.sol';
import './interfaces/IAssetRegistry.sol';
import './interfaces/IEntityRegistry.sol';

import 'zeppelin-solidity/contracts/math/SafeMath.sol';


contract Settlement is ContractProviderEnabled, Limited {
    using SafeMath for uint256;

    uint256 constant public approvalDeadline = 12 hours;

    struct Transaction {
        bytes32 sellerId;
        bytes32 buyerId;
        bytes32 assetId;
        uint256 numberOfShares;
        uint256 price;
        uint256 timestamp;
        address sender;
        uint256 deadline;
        bool isApproved;
    }

    // Status of pending approvals
    mapping(uint256 => mapping(bytes32 => bool)) public pendingApprovals;

    // Number of approvals required to save the transaction
    mapping(uint256 => uint256) public approvalsRemaining;

    mapping(uint256 => Transaction) public pendingTransactions;
    uint256[] public pendingTransactionsLUT;

    uint256 private pendingTransactionCounter = 1;

    mapping(uint256 => Transaction) public transactions;
    uint256[] public transactionsLUT;

    uint256 private transactionCounter = 1;

    event LogNewPendingTransaction(
        uint256 transactionId,
        bytes32 sellerId,
        bytes32 buyerId,
        bytes32 assetId,
        uint256 numberOfShares,
        uint256 indexed price,
        uint256 indexed date,
        address sender,
        uint256 deadline
    );

    event LogNewTransactionByMetadata(
        uint256 indexed transactionId,
        bytes32 sellerId,
        bytes32 buyerId,
        bytes32 assetId,
        uint256 numberOfShares,
        uint256 indexed price,
        uint256 indexed now,
        address sender
    );

    event LogNewTransactionByParticipants(
        uint256 transactionId,
        bytes32 indexed sellerId,
        bytes32 indexed buyerId,
        bytes32 indexed assetId,
        uint256 numberOfShares,
        uint256 price,
        uint256 now,
        address sender
    );

    event LogInitiateReverseTransaction(
        uint256 indexed transactionCounter,
        address sender
    );

    event LogNewApproval(
        uint256 pendingTxCounter,
        address sender
    );

    function validateTransaction(
        bytes32 sellerId,
        bytes32 buyerId,
        bytes32 assetId,
        uint256 numberOfShares
    )
        internal
        view
        returns(bool)
    {
        address assetRegistryAddr = IContractProvider(cp).contracts('asset_registry');
        require(IAssetRegistry(assetRegistryAddr).validateSeller(assetId, sellerId, numberOfShares));

        address entityRegistryAddr = IContractProvider(cp).contracts('entity_registry');
        require(IEntityRegistry(entityRegistryAddr).confirmRegisteredEntity(buyerId));

        return true;
    }

    function initiateTransaction(
        bytes32 sellerId,
        bytes32 buyerId,
        bytes32 assetId,
        uint256 numberOfShares,
        uint256 price
    )
        public
        performs(NetworkActivity.InitiateTransaction)
        returns (bool)
    {
        require(sellerId != '');
        require(buyerId != '');
        require(assetId != '');
        require(numberOfShares != 0);

        // outsource validation, stack too deep
        validateTransaction(
            sellerId,
            buyerId,
            assetId,
            numberOfShares
        );

        // setup approval structure
        setupApprovals(assetId, sellerId, buyerId);

        pendingTransactions[pendingTransactionCounter] =
            Transaction({
                sellerId: sellerId,
                buyerId: buyerId,
                assetId: assetId,
                numberOfShares: numberOfShares,
                price: price,
                timestamp: now,
                sender: msg.sender,
                deadline: now + approvalDeadline,
                isApproved: false
            });
        pendingTransactionsLUT.push(pendingTransactionCounter);

        LogNewPendingTransaction(
            pendingTransactionCounter,
            sellerId,
            buyerId,
            assetId,
            numberOfShares,
            price,
            now,
            msg.sender,
            now + approvalDeadline
        );
        pendingTransactionCounter++;
    }

    function setupApprovals(bytes32 assetId, bytes32 sellerId, bytes32 buyerId)
        internal
        returns (bool)
    {

        address entityRegistryAddress = IContractProvider(cp).contracts('entity_registry');
        address ownerSeller = IEntityRegistry(entityRegistryAddress).getOwner(sellerId);
        address ownerBuyer = IEntityRegistry(entityRegistryAddress).getOwner(buyerId);

        // If the sender isn't seller entity owner or a delegate, we need their approval
        if (msg.sender != ownerSeller
            && !IEntityRegistry(entityRegistryAddress).delegates(sellerId, msg.sender)) {
            pendingApprovals[pendingTransactionCounter][sellerId] = true;
            approvalsRemaining[pendingTransactionCounter] += 1;
        }

        // If the sender isn't buyer entity owner or a delegate, we need their approval
        if (msg.sender != ownerBuyer 
            && !IEntityRegistry(entityRegistryAddress).delegates(buyerId, msg.sender)) {
            pendingApprovals[pendingTransactionCounter][buyerId] = true;
            approvalsRemaining[pendingTransactionCounter] += 1;
        }
        address assetRegistryAddr = IContractProvider(cp).contracts('asset_registry');

        // entities listed in the asset registry must approve 
        uint256 numApprovals = IAssetRegistry(assetRegistryAddr).getApprovalsCount(assetId);

        for (uint8 i = 0; i < numApprovals; i++) {
            bytes32 entityId = IAssetRegistry(assetRegistryAddr).getRequiredApproval(assetId, i);
            if (!pendingApprovals[pendingTransactionCounter][entityId]) {
                pendingApprovals[pendingTransactionCounter][entityId] = true;
                approvalsRemaining[pendingTransactionCounter] += 1;
            }
        }

        return true;

    }

    // Check whether or not a pending transaction has all of the approvals it
    // requires by observing the isApproved field and the number of remaining
    // approvals required
    function isApproved(uint256 pendingTxCounter) public returns(bool) {
        Transaction storage pTx = pendingTransactions[pendingTxCounter];
        if (pTx.isApproved) return true;
        if (approvalsRemaining[pendingTxCounter] > 0) return false; 
        pTx.isApproved = true;
        return true;
    }

    // Users must mention the identity on behalf of whom they are approving the transaction
    function approveTransactionFor(uint256 pendingTxCounter, bytes32 entityId)
        public
        returns (bool)
    {
        // The transaction must be awaiting approval from this approval
        require(pendingApprovals[pendingTxCounter][entityId]);

        Transaction storage pTx = pendingTransactions[pendingTxCounter];

        // Make sure that the transaction index is valid 
        require(pTx.sellerId != '');
        require(pTx.buyerId != '');
        require(pTx.deadline >= now);

        // If the transaction is alread approved, why are you calling this method?
        require(!pTx.isApproved);

        // You must mention an identity
        require(entityId != '');

        // Make sure the user has the token holdings to do this 
        address limiterAddr = IContractProvider(cp).contracts('activity_limiter');
        NetworkActivity activity = NetworkActivity.ApproveTransaction;

        if (pTx.sellerId == entityId || pTx.buyerId == entityId) {
            activity = NetworkActivity.ApproveTransactionAsParticipant;
        }

        IActivityLimiter(limiterAddr).performAction(activity);

        // The sender should either be the owner of the mentioned identity or a delegate
        address entityRegistryAddr = IContractProvider(cp).contracts('entity_registry');
        address entityOwner = IEntityRegistry(entityRegistryAddr).getOwner(entityId);

        require(entityOwner == msg.sender
            || IEntityRegistry(entityRegistryAddr).delegates(entityId, msg.sender));

        // Update the approval status, decrease the number of missing
        // approvals, and possibly save the transaction
        pendingApprovals[pendingTxCounter][entityId] = false; 
        approvalsRemaining[pendingTxCounter]--;
        LogNewApproval(pendingTxCounter, msg.sender);
        if(isApproved(pendingTxCounter)) saveTransaction(pendingTxCounter);

        return true;
    }

    function saveTransaction(uint256 pendingTxCounter)
        internal
        returns (bool)
    {

        Transaction memory pTx = pendingTransactions[pendingTxCounter];
        require(pTx.isApproved);

        validateTransaction(
            pTx.sellerId,
            pTx.buyerId,
            pTx.assetId,
            pTx.numberOfShares
        );

        address assetRegistryAddr = IContractProvider(cp).contracts('asset_registry');

        updateAssetRegistry(pTx.sellerId, pTx.buyerId, pTx.assetId, pTx.numberOfShares, assetRegistryAddr);

        transactions[transactionCounter] = pTx;
        transactions[transactionCounter].timestamp = now;
        transactionsLUT.push(transactionCounter);
        LogNewTransactionByMetadata(
            transactionCounter,
            pTx.sellerId,
            pTx.buyerId,
            pTx.assetId,
            pTx.numberOfShares,
            pTx.price,
            now,
            pTx.sender
        );

        LogNewTransactionByParticipants(
            transactionCounter,
            pTx.sellerId,
            pTx.buyerId,
            pTx.assetId,
            pTx.numberOfShares,
            pTx.price,
            now,
            pTx.sender
        );

        delete pendingTransactions[pendingTxCounter];
        transactionCounter++;
    }

    // Start the process of reversing a transaction
    function initiateReverseTransaction(uint256 transactionId)
        public
        performs(NetworkActivity.ReverseTransaction)
        returns (bool)
    {
        Transaction storage t = transactions[transactionId];

        initiateTransaction(
            t.buyerId,
            t.sellerId,
            t.assetId,
            t.numberOfShares,
            t.price
        );
        LogInitiateReverseTransaction(pendingTransactionCounter - 1, msg.sender);
        return true;
    }

    function getTransactionCount() public view returns (uint256) {
        return transactionsLUT.length;
    }

    function getTransaction(uint256 transactionId)
        public
        view
        returns (
            bytes32 sellerId,
            bytes32 buyerId,
            bytes32 assetId,
            uint256 numberOfShares,
            uint256 price,
            uint256 timestamp
        )
    {
        require(transactionId != 0);
        Transaction storage t = transactions[transactionId];
        return (t.sellerId, t.buyerId, t.assetId, t.numberOfShares, t.price, t.timestamp);
    }

    function updateAssetRegistry(
        bytes32 sellerId,
        bytes32 buyerId,
        bytes32 assetId,
        uint256 numberOfShares,
        address assetRegistryAddr
    )
        internal
        returns(bool)
    {
        IAssetRegistry AssetRegistry = IAssetRegistry(assetRegistryAddr);

        AssetRegistry.increaseHoldings(assetId, buyerId, numberOfShares);
        AssetRegistry.decreaseHoldings(assetId, sellerId, numberOfShares);

        return true;
    }
}
